import React, { useEffect } from 'react';
import './Sakura.css'; // 包含背景和字体样式

const SakuraAnimation = () => {
  useEffect(() => {
    const images = [
        "data:image/png;base64,",
        "data:image/png;base64,",
        "data:image/png;base64,",
        "data:image/png;base64,",
        "data:image/png;base64,",
    ];

    let requestId;
    const canvas = document.createElement('canvas');
    canvas.id = 'canvas_sakura';
    canvas.style.position = 'absolute';
    canvas.style.top = 0;
    canvas.style.left = 0;
    canvas.style.pointerEvents = 'none';
    canvas.style.zIndex = 0; // 设置为最底层
    document.body.appendChild(canvas);

    const cxt = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    function getRandom(option) {
      let ret, random;
      switch (option) {
        case 'x':
          ret = Math.random() * window.innerWidth;
          break;
        case 'y':
          ret = Math.random() * window.innerHeight;
          break;
        case 's':
          ret = Math.random();
          break;
        case 'r':
          ret = Math.random() * 6;
          break;
        case 'fnx':
          random = -0.5 + Math.random() * 1;
          ret = function (x, y) {
            return x + 0.5 * random - 0.6;
          };
          break;
        case 'fny':
          random = 0.8 + Math.random() * 0.7;
          ret = function (x, y) {
            return y + random;
          };
          break;
        case 'fnr':
          random = Math.random() * 0.03;
          ret = function (r) {
            return r + random;
          };
          break;
        default:
          ret = 0;
      }
      return ret;
    }

    function Sakura(x, y, s, r, fn, image) {
      this.x = x;
      this.y = y;
      this.s = s;
      this.r = r;
      this.fn = fn;
      this.image = image;
    }

    Sakura.prototype.draw = function (cxt) {
      cxt.save();
      const img = new Image();
      img.src = this.image;
      cxt.translate(this.x, this.y);
      cxt.rotate(this.r);
      cxt.drawImage(img, 0, 0, 35 * this.s, 35 * this.s);
      cxt.restore();
    };

    Sakura.prototype.update = function () {
      this.x = this.fn.x(this.x, this.y);
      this.y = this.fn.y(this.y, this.y);
      this.r = this.fn.r(this.r);
      if (this.x > window.innerWidth || this.x < 0 || this.y > window.innerHeight || this.y < 0) {
        this.x = getRandom('x');
        this.y = 0;
        this.s = getRandom('s');
        this.r = getRandom('r');
      }
    };

    const sakuraList = [];
    for (let i = 0; i < 10; i++) {
      const sakura = new Sakura(
        getRandom('x'),
        getRandom('y'),
        getRandom('s'),
        getRandom('r'),
        {
          x: getRandom('fnx'),
          y: getRandom('fny'),
          r: getRandom('fnr')
        },
        images[Math.floor(Math.random() * images.length)]
      );
      sakuraList.push(sakura);
    }

    function drawSakura() {
      cxt.clearRect(0, 0, canvas.width, canvas.height);
      sakuraList.forEach((sakura) => sakura.draw(cxt));
      sakuraList.forEach((sakura) => sakura.update());
      requestId = requestAnimationFrame(drawSakura);
    }

    drawSakura();

    window.onresize = function () {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    return () => {
      cancelAnimationFrame(requestId);
      document.body.removeChild(canvas);
    };
  }, []);

  return null;
};

export default SakuraAnimation;